#!/usr/bin/env node
const commander = require('commander');
const inquirer = require('inquirer');

const actionHandler = () => {
    const prompts = [
        {
            type: 'input',
            name: 'username',
            message: '请输入你得名字'
        },
        {
            type: 'list',
            choices: ['male', 'female'],
            name: 'gender',
            message: '请输入你得性别'
        },
        {
            type: 'number',
            name: 'age',
            message: '请输入你得年龄'
        },
        {
            type: 'input',
            name: 'address',
            message: '请输入你得家庭住址'
        },
        {
            type: 'checkbox',
            name: 'hobby',
            message: '请选择你得爱好',
            choices: ['吃饭饭', '睡觉觉', '打豆豆']
        },
        {
            type: 'confirm',
            name: 'confirm',
            message: '确定要提交吗？'
        }
    ];
    inquirer.prompt(prompts).then(res => {
        if (res.confirm) {
            console.log(res);
        } else {
            console.log('取消！');
        }
    });
};
commander
    .version("1.2.3")
    .command('init')
    .action(actionHandler);
commander.parse(process.argv);
